import React from "react";
import { Container, Row, Col, Card, CardBody, Form,
  FormInput,
  FormGroup,Button } from "shards-react";
import { appConfig } from '../utils'

import { PostDisplay } from '../components/posts/PostDisplay';
const { useEffect, useState } = React;

const Posts = (props) => {
  let userType = "User";
  const { path } = props.match;
  const [userId, setUserId] = useState();
  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [phoneNo, setPhoneNo] = useState();
  const [status, setStatus] = useState();
  const [createdDate, setCreatedDate] = useState();

  // this.handleSubmit = this.handleSubmit.bind(this);
  // this.handleChange = this.handleChange.bind(this);

  const handleSubmit = (event) => {
    event.preventDefault();
    const Url = appConfig.webApi + "/users/saveUsers";
    fetch(Url, {
      method: 'post',
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        'Access-Control-Allow-Origin': '*'
      },
      body: JSON.stringify({
        // "userId":this.state.userId,
        // "firstName":this.state.firstName,
        // "lastName":this.state.lastName,
        // "email":this.state.email,
        // "password":this.state.password,
        // "phoneNo":this.state.phoneNo,
        // "status":this.state.status,
        // "createdDate":this.state.createdDate
        "userId": userId,
        "firstName": firstName,
        "lastName": lastName,
        "email": email,
        "password": password,
        "phoneNo": phoneNo,
        "status": status,
        "createdDate": createdDate
      })
    }).then(function (response) {
      return response.json();
    }).then(function (responseData) {
      if (responseData.status == "Inavalid details") {
        alert(responseData.status);
      } else {
        console.log(responseData)
        // localStorage.setItem("userData",JSON.stringify(responseData));
        //  window.location.href="/dashboard";
      }

    })
  }

 const handleChange=(e)=> {
    const obj = {};
    console.log()
    if (e.target.name == "userId") {
      setUserId(e.target.value)
    }else if(e.target.name == "firstName"){
      setFirstName(e.target.value)
    }else if(e.target.name == "lastName"){
      setLastName(e.target.value)
    }else if(e.target.name == "email"){
      setEmail(e.target.value)
    }else if(e.target.name == "password"){
      setPassword(e.target.value)
    }else if(e.target.name == "phoneNo"){
      setPhoneNo(e.target.value)
    }else if(e.target.name == "status"){
      setStatus(e.target.value)
    }else if(e.target.name == "createdDate"){
      setcreatedDate(e.target.value)
    }
    // obj[e.target.name] = e.target.value;
    // this.setState(obj);
  }

  const [posts, setPosts] = useState([]);
  useEffect(() => {
    const postUrl = appConfig.webApi + "/userList/getUserList";
    fetch(postUrl, {
      method: 'post',
      //   headers: {
      //       Accept: "application/json",
      //       "Content-Type": "application/json",
      //     'Access-Control-Allow-Origin': '*'
      // },
      body: JSON.stringify(null)
    }).then(function (response) {
      return response.json();
    }).then(function (responseData) {
      console.log(responseData);
      setPosts(responseData);
    });
  }, [])
  return (
    <>
      <Form style={{ marginTop: "2%" }} onSubmit={handleSubmit}>
        <FormGroup>
          <FormInput required onChange={handleChange} name="userId" placeholder="User Id" />
        </FormGroup>
        <FormGroup>
          <FormInput required onChange={handleChange} name="firstName" placeholder="First Name" />
        </FormGroup>
        <FormGroup>
        <FormInput required onChange={handleChange} name="lastName" placeholder="Lat Name" />
       </FormGroup>
      <FormGroup>
        <FormInput required onChange={handleChange} name="email" placeholder="Email" />
      </FormGroup>
      <FormGroup>
      <FormInput required onChange={handleChange} name="password" placeholder="Password" type="password" />
    </FormGroup>
    <FormGroup>
      <FormInput required onChange={handleChange} name="phoneNo" placeholder="Phone No" />
    </FormGroup>
    <FormGroup>
    <FormInput required onChange={handleChange} name="status" placeholder="Status" />
        </FormGroup >
  <FormGroup>
    <FormInput required onChange={handleChange} name="createdDate" placeholder="CreatedDate" />
  </FormGroup>
  <Button theme="success" type="submit">Add</Button>
      </Form >

  <Container fluid className="main-content-container px-4">
    <Row>
      <Col>
        <Card small className="mb-4" style={{ marginTop: "1%" }}>
          {/*  <CardHeader className="border-bottom">
            <h6 className="m-0">Active Users</h6>
          </CardHeader> */}
          <CardBody className="p-0 pb-3">
            <table className="table mb-0">
              <thead className="bg-light">
                <tr>
                  <th scope="col" className="border-0">
                    UserId
                  </th>
                  <th scope="col" className="border-0">
                    FirstName
                  </th>
                  <th scope="col" className="border-0">
                    LastName
                  </th>
                  <th scope="col" className="border-0">
                    Email
                  </th>
                  <th scope="col" className="border-0">
                    PhoneNo
                  </th>
                  <th scope="col" className="border-0">
                    Status
                  </th>
                  <th scope="col" className="border-0">
                    Created Date
                  </th>
                </tr>
              </thead>
              <tbody>
                {posts.map((p, index) => (
                  <PostDisplay post={p} key={index} />
                ))}
              </tbody>
            </table>
          </CardBody>
        </Card>
      </Col>
    </Row>
  </Container>
</>
);
};

export default Posts;
